import React from 'react';
import { NavLink } from 'react-router-dom';
// import menuToggle from '../menutoggle.js';
import LocalMallOutlinedIcon from '@material-ui/icons/LocalMallOutlined';
const Navbar = () => {

    window.onload = (props) => {
        
    const menuToggle = () => {
        const menu = document.getElementById('menu');
        const menuList = document.getElementsByClassName('menuList');
    
        if (menu.style.maxHeight === '0px') {
            menu.style.maxHeight = '280px';
        } else {
            menu.style.maxHeight = '0px';
        }
    
    
        for (var i = 0; i < menuList.length; i++) {
            if (menuList[i].style.display === 'none') {
                menuList[i].style.display = 'block';
            } else {
                menuList[i].style.display = 'none';
            }
        }
    }
    }

    const scrollTop = () => {
        window.scrollTo(0, 0);
    }


    return (
        <>
            <div className='nav_div'>
                <div className='container'>
                    <div className='navbar'>
                        <div className='logo'>
                            <NavLink exact to='/' activeClassName='active_class'>
                                <img src={require('../images/logo.png')} alt='logo' />
                            </NavLink>
                        </div>
                        <nav>
                            <ul id='menu'>
                                <li className='menuList' onClick={scrollTop}><NavLink exact to='/' activeClassName='active_class'>Home</NavLink></li>
                                <li className='menuList' onClick={scrollTop}><NavLink exact to='/products' activeClassName='active_class'>Products</NavLink></li>
                                <li className='menuList' onClick={scrollTop}><NavLink exact to='/about' activeClassName='active_class'>About</NavLink></li>
                                <li className='menuList' onClick={scrollTop}><a href='#contact'>Contact</a></li>
                                <li className='menuList' onClick={scrollTop}><NavLink exact to='/account' activeClassName='active_class'>Account</NavLink></li>
                                <li className='menuList' onClick={scrollTop}>
                                    <NavLink exact to='/cart' activeClassName='active_class'>
                                        <a href='' className='cart' onClick={scrollTop}><LocalMallOutlinedIcon /></a>
                                    </NavLink>
                                </li>

                            </ul>
                        </nav>
                        <img src={require('../images/menu.png')} alt='menuIcon' className='menu_icon' onClick={props.menuToggle} />
                    </div>
                </div>
            </div>
        </>
    )
}
export default Navbar;
