import React from 'react';
import { NavLink } from 'react-router-dom';
import menuToggle from './menutoggle.js';
import LocalMallOutlinedIcon from '@material-ui/icons/LocalMallOutlined';
const Navbar = () => {

    const scrollTop = () => {
        window.scrollTo(0, 0);
    }

    function reloadPage() {
        var currentDocumentTimestamp = new Date(performance.timing.domLoading).getTime();
        // Current Time //
        var now = Date.now();
        // Total Process Lenght as Minutes //
        var tenSec = 10 * 1000;
        // End Time of Process //
        var plusTenSec = currentDocumentTimestamp + tenSec;
        if (now > plusTenSec) {
            window.location.reload();
        }
    }


    // You can call it in somewhere //
    reloadPage();

    return (
        <>
            <div className='nav_div'>
                <div className='container'>
                    <div className='navbar'>
                        <div className='logo'>
                            <NavLink exact to='/' activeClassName='active_class'>
                                <img src={require('../images/logo.png')} alt='logo' onClick={scrollTop}/>
                            </NavLink>
                        </div>
                        <nav>
                            <ul id='menu'>
                                <li className='menuList' onClick={scrollTop}><NavLink exact to='/' activeClassName='active_class'>Home</NavLink></li>
                                <li className='menuList' onClick={scrollTop}><NavLink exact to='/products' activeClassName='active_class'>Products</NavLink></li>
                                <li className='menuList' onClick={scrollTop}><NavLink exact to='/about' activeClassName='active_class'>About</NavLink></li>
                                <li className='menuList' onClick={scrollTop}><a href='#contact' id='contact_id'>Contact</a></li>
                                <li className='menuList' onClick={scrollTop}><NavLink exact to='/account' activeClassName='active_class'>Account</NavLink></li>
                                <li className='menuList' onClick={scrollTop}>
                                    <NavLink exact to='/cart' activeClassName='active_class'>
                                        <a href='' className='cart' onClick={scrollTop}><LocalMallOutlinedIcon /></a>
                                    </NavLink>
                                </li>

                            </ul>
                        </nav>
                        <img src={require('../images/menu.png')} alt='menuIcon' className='menu_icon' onClick={menuToggle} />
                    </div>
                </div>
            </div>
        </>
    )
}
export default Navbar;
