import React from 'react';
import ShoppingCartOutlinedIcon from '@material-ui/icons/ShoppingCartOutlined';

const Home = () => {


    // let menu = document.getElementById('menu');

    // menu.style.maxHeight = '0px';

    // const menuToggle = () => {

    //     if (document.getElementById('menu').style.maxHeight = '0px') {
    //         document.getElementById('menu').style.maxHeight = '250px';
    //     } else {
    //         document.getElementById('menu').style.maxHeight = '0px';
    //     }
    // } 

    const menuToggle = () => {
        const menu = document.getElementById('menu');
        const menuList = document.getElementsByClassName('menuList');

       if(menu.style.maxHeight === '0px') {
           menu.style.maxHeight = '230px';
       }else{
        menu.style.maxHeight = '0px';
       }

       for(var i= 0; i<menuList.length; i++) {
           if(menuList[i].style.display === 'none'){
            menuList[i].style.display = 'block';
           } else {
            menuList[i].style.display = 'none';
           }
       }

        
    }


    return (
        <>
            <div className='home_div'>
                <div className='container'>
                    <div className='navbar'>
                        <div className='logo'>
                            <img src={require('../images/logo.png')} alt='logo' />
                        </div>
                        <nav>
                            <ul id='menu'>
                                <li className='menuList'><a href=''>Home</a></li>
                                <li className='menuList'><a href=''>Products</a></li>
                                <li className='menuList'><a href=''>About</a></li>
                                <li className='menuList'><a href=''>Contact</a></li>
                                <li className='menuList'><a href=''>Account</a></li>
                            </ul>
                        </nav>
                        <a href='' className='cart'><ShoppingCartOutlinedIcon /></a>
                        <img src={require('../images/menu.png')} alt='menuIcon' className='menu_icon' onClick={menuToggle} />
                    </div>

                    <div className='hero'>
                        <div className='hero-col'>
                            <h1>Boost Your Gameplay<br />With These Accessories.</h1>
                            <p>You were born to be a player. You were meant to be here.<br /> This moment is yours.</p>
                            <a className='more_btn'>Go Shopping</a>
                        </div>
                        <div className='hero-col'>
                            <img src={require('../images/back.png')} alt='homebackground' />
                        </div>
                    </div>

                </div>
            </div>
        </>
    )

}

export default Home;
