import React from 'react';
import { Route, Switch, BrowserRouter } from 'react-router-dom'
import Home from './Home/Home.jsx';
import Products from './Products/Products.jsx';
import ProductDetails from './Products/ProductDetails';
import About from './About/About';
import Cart from './MyCart/Cart';
import Account from './Account/Account';

const App = () => {
    return (
        <>
            <BrowserRouter>
                <Switch>
                    <Route exact path='/yourcart' component={Home} />
                    <Route exact path='/yourcart/products' component={Products} />
                    <Route exact path='/yourcart/productDetails' component={ProductDetails} />
                    <Route exact path='/yourcart/about' component={About}/>
                    {/* <Route exact path='/contact' component={Contact}/> */}
                    <Route exact path='/yourcart/account' component={Account} />
                    <Route exact path='/yourcart/cart' component={Cart} />
                </Switch>
            </BrowserRouter>

            {/* <Home /> */}
            {/* <Products /> */}
            {/* <ProductDetails /> */}
            {/* <Cart /> */}
            {/* <Account /> */}
        </>
    )
}

export default App;
